import React, { createContext, useState, useEffect, useContext } from 'react';
import { initializeSocket, disconnectSocket } from '../services/socket';
import { useAuth } from '../context/AuthContext';

export const TaskContext = createContext();

export const TaskProvider = ({ children }) => {
  const [tasks, setTasks] = useState([]);
  const [socket, setSocket] = useState(null);
  const { user, getToken } = useAuth();

  useEffect(() => {
    if (user) {
      const token = getToken();
      const newSocket = initializeSocket(token);
      setSocket(newSocket);

      // Listen for task updates
      newSocket.on('sync:tasks', (initialTasks) => {
        setTasks(initialTasks);
      });

      newSocket.on('task:created', (newTask) => {
        setTasks(prev => [...prev, newTask]);
      });

      newSocket.on('task:updated', (updatedTask) => {
        setTasks(prev => prev.map(task => 
          task.id === updatedTask.id ? updatedTask : task
        ));
      });

      newSocket.on('task:moved', (movedTask) => {
        setTasks(prev => prev.map(task => 
          task.id === movedTask.id ? movedTask : task
        ));
      });

      newSocket.on('task:deleted', (taskId) => {
        setTasks(prev => prev.filter(task => task.id !== taskId));
      });

      return () => {
        if (newSocket) {
          newSocket.off('sync:tasks');
          newSocket.off('task:created');
          newSocket.off('task:updated');
          newSocket.off('task:moved');
          newSocket.off('task:deleted');
        }
      };
    } else {
      if (socket) {
        disconnectSocket();
        setSocket(null);
      }
      setTasks([]);
    }
  }, [user]);

  const addTask = (taskData) => {
    if (socket) {
      socket.emit('task:create', taskData);
    }
  };

  const updateTask = (taskId, updates) => {
    if (socket) {
      socket.emit('task:update', { id: taskId, ...updates });
    }
  };

  const moveTask = (taskId, newColumn) => {
    if (socket) {
      socket.emit('task:move', { taskId, newColumn });
    }
  };

  const deleteTask = (taskId) => {
    if (socket) {
      socket.emit('task:delete', taskId);
    }
  };

 return (
    <TaskContext.Provider value={{
      tasks,
      addTask,
      updateTask,
      moveTask,
      deleteTask,
      socket  // we are providing socket here
    }}>
      {children}
    </TaskContext.Provider>
  );
};
